@import url(https://fonts.googleapis.com/css?family=Nunito+Sans);:root{--blue:#0e0620;--white:#fff;--green:#2ccf6d}body,html{height:100%}body{display:-webkit-box;display:flex;-webkit-box-align:center;align-items:center;-webkit-box-pack:center;justify-content:center;font-family:"Nunito Sans";color:var(--blue);font-size:1em}button{font-family:"Nunito Sans"}ul{list-style-type:none;-webkit-padding-start:35px;padding-inline-start:35px}svg{width:100%;visibility:hidden}h1{font-size:7.5em;margin:15px 0;font-weight:700}h2{font-weight:700}.hamburger-menu{position:absolute;top:0;left:0;padding:35px;z-index:2}.hamburger-menu button{position:relative;width:30px;height:22px;border:none;background:0 0;padding:0;cursor:pointer}.hamburger-menu button span{position:absolute;height:3px;background:#000;width:100%;left:0;top:0;-webkit-transition:.1s ease-in;transition:.1s ease-in}.hamburger-menu button span:nth-child(2){top:9px}.hamburger-menu button span:nth-child(3){top:18px}.hamburger-menu [data-state=open] span:first-child{-webkit-transform:rotate(45deg);transform:rotate(45deg);top:10px}.hamburger-menu [data-state=open] span:nth-child(2){width:0%;opacity:0}.hamburger-menu [data-state=open] span:nth-child(3){-webkit-transform:rotate(-45deg);transform:rotate(-45deg);top:10px}nav{position:absolute;height:100%;top:0;left:0;background:var(--green);color:var(--blue);width:300px;z-index:1;padding-top:80px;-webkit-transform:translateX(-100%);transform:translateX(-100%);-webkit-transition:.24s cubic-bezier(.52,.01,.8,1);transition:.24s cubic-bezier(.52,.01,.8,1)}nav li{-webkit-transform:translateX(-5px);transform:translateX(-5px);-webkit-transition:.16s cubic-bezier(.44,.09,.46,.84);transition:.16s cubic-bezier(.44,.09,.46,.84);opacity:0}nav a{display:block;font-size:1.75em;font-weight:700;text-decoration:none;color:inherit;-webkit-transition:.24s ease-in-out;transition:.24s ease-in-out}nav a:hover{text-decoration:none;color:var(--white)}nav[data-state=open]{-webkit-transform:translateX(0);transform:translateX(0)}nav[data-state=open] ul li:nth-child(1){-webkit-transition-delay:.16s;transition-delay:.16s;-webkit-transform:translateX(0);transform:translateX(0);opacity:1}nav[data-state=open] ul li:nth-child(2){-webkit-transition-delay:.32s;transition-delay:.32s;-webkit-transform:translateX(0);transform:translateX(0);opacity:1}nav[data-state=open] ul li:nth-child(3){-webkit-transition-delay:.48s;transition-delay:.48s;-webkit-transform:translateX(0);transform:translateX(0);opacity:1}nav[data-state=open] ul li:nth-child(4){-webkit-transition-delay:.64s;transition-delay:.64s;-webkit-transform:translateX(0);transform:translateX(0);opacity:1}.btn{z-index:1;overflow:hidden;background:0 0;position:relative;padding:8px 50px;border-radius:30px;cursor:pointer;font-size:1em;letter-spacing:2px;-webkit-transition:.2s ease;transition:.2s ease;font-weight:700;margin:5px 0}.btn.green{border:4px solid var(--green);color:var(--blue)}.btn.green:before{content:"";position:absolute;left:0;top:0;width:0%;height:100%;background:var(--green);z-index:-1;-webkit-transition:.2s ease;transition:.2s ease}.btn.green:hover{color:var(--white);background:var(--green);-webkit-transition:.2s ease;transition:.2s ease}.btn.green:hover:before{width:100%}@media screen and (max-width:768px){body{display:block}.container{margin-top:70px;margin-bottom:70px}}
